export class Guest {
  private id: number;
  private name: string;
  private email: string;
  private phoneNumber: string;

  constructor(id: number, name: string, email: string, phoneNumber: string) {
    this.id = id;
    this.name = name;
    this.email = email;
    this.phoneNumber = phoneNumber;
  }

  public getId(): number {
    return this.id;
  }

  public getName(): string {
    return this.name;
  }

  public getEmail(): string {
    return this.email;
  }

  public getPhoneNumber(): string {
    return this.phoneNumber;
  }

  public updateEmail(newEmail: string): void {
    this.email = newEmail;
  }

  public updatePhoneNumber(newPhoneNumber: string): void {
    this.phoneNumber = newPhoneNumber;
  }
  public postGuest(newGuest:Guest) {
    fetch('http://localhost:4000/guests', {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(newGuest)
    })
      .then((response) => response.json())
      .then((addedGuest: Guest) => {
        console.log("Room updated:", newGuest);
      })
      .catch((error) => {
        console.error(error);
      });
  }
}
