import React, { useState } from "react";
import styles from "./Workers.module.css";
import {
  Manager,
  WorkersCommand as workers,
  AddWorkersCommand,
  FireWorkersCommand,
} from "./WorkersCommand.js";
import Button from "../UI/Button/Button";
import Login from "../Authentication/Login";
import WorkersAdd from "./WorkersAdd";
import WorkersCheck from "./WorkersCheck";
import WorkersFire from "./WorkersFire";

const Workers = (props) => {
  const [addEmplyeeButtonActive, setAddEmplyeeButtonActive] = useState(true);
  const [fireEmplyeeButtonActive, setFireEmplyeeButtonActive] = useState(false);
  const [checkEmplyeeButtonActive, setCheckEmplyeeButtonActive] =
    useState(false);
  //   const manager = new Manager();
  //   const worker = new workers();
  //   manager.SetCommand(new AddWorkersCommand(worker, "Andrii", "principle"));
  //   manager.ExecuteCommands();
  //   worker.CheckList();
  //   manager.SetCommand(new AddWorkersCommand(worker, "у", "у"));
  //   manager.ExecuteCommands();
  //   worker.CheckList();
  //   manager.SetCommand(new FireWorkersCommand(worker, "у"));
  //   manager.ExecuteCommands();
  //   worker.CheckList();
  function onClickAddHandler() {
    setAddEmplyeeButtonActive((prev) => {
      return prev === true ? false : false;
    });
  }
  function onClickFireHandler() {
    setFireEmplyeeButtonActive((prev) => {
      return prev === true ? true : false;
    });
  }
  function onClickCheckHandler() {
    setCheckEmplyeeButtonActive((prev) => {
      return prev === true ? true : false;
    });
  }
  return (
    <div className={styles.workersContainer}>
      <h2>Workers management section</h2>
      {/* <Login></Login> */}
      <div className={styles.buttonsContainer}>
        <Button onClick={onClickAddHandler}>Add Employee</Button>
        <Button onClick={onClickFireHandler}>Fire Employee</Button>
        <Button onClick={onClickCheckHandler}>Check Employees</Button>
      </div>
      {addEmplyeeButtonActive && <WorkersAdd></WorkersAdd>}
      {fireEmplyeeButtonActive && <WorkersFire></WorkersFire>}
      {checkEmplyeeButtonActive && <WorkersCheck></WorkersCheck>}
    </div>
  );
};
export default Workers;
