import React, { useState } from "react";
import styles from "./Payments.module.css";
import { Composite, poolPayment } from "./PaymentsComposite.ts";

const Payments = (props) => {
  // const spa = new Composite(); // Pass the desired arguments to the constructor
  // const hotel = new Composite();
  // console.log(spa.name);
  // const pool = new poolPayment(400);
  // console.log(pool.amountService);
  // const pool2 = new poolPayment(300);
  // spa.AddComponent(pool);
  // spa.AddComponent(pool2);
  // console.log(spa.getAmount);
  // spa.addPayment(33);
  // console.log(spa.getAmount);
  // spa.issueRefund(33);
  // console.log(spa.getAmount);

  return (
    <div className={styles.reservationContainer}>
      <h2>Reservation section</h2>
      <form onSubmit={onSubmitFormHandler}>
        <label htmlFor="nameUser">Your Name:</label>
        <input type="text" name="nameUser" onChange={onChangeName}></input>
        <label htmlFor="surnameUser">Your Surname:</label>
        <input
          type="text"
          name="surnameUser"
          onChange={onChangeSurname}
        ></input>
        <label htmlFor="cityUser">Your phone number:</label>
        <input type="text" name="cityUser" onChange={onChangeCity}></input>
        <label htmlFor="idUser">Passport ID:</label>
        <input type="number" name="idUser" onChange={onChangeID}></input>
        <label htmlFor="roomUser">Room's type:</label>
        <div>
          <select
            name="roomUser"
            className={styles.sectionBox}
            onChange={onChangePriceHandler}
          >
            <option value="100">Standart room</option>
            <option value="200">Middle room</option>
            <option value="300">Luxury room</option>
          </select>
          <p>Price: {priceRoom}$</p>
        </div>
        <Button type="submit">Reservation</Button>
      </form>
    </div>
  );
};
export default Payments;
