import React, { useState } from "react";
import styles from "./Login.module.css";
import Button from "../UI/Button/Button.js";
import { User, AuthenticationService } from "./AuthenticationSingleton.ts";

const Login = (props) => {
  const [registerEmail,setRegisterEmail]=useState('');
  const [registerPassword,setRegisterPassword]=useState('');

  function onContinueHandler(event){
    event.preventDefault();
    const authService = AuthenticationService.getInstance();
    props.authServices.login("johnDoe", "password12");
  }
  function onSignUpHandler(event){
    event.preventDefault();
    props.onSignUp();
  }
  return (
    <div className={styles.formbg}>
      <div className={styles["padding-horizontal--48"]}>
        <span className={styles["padding-bottom--15"]}>
          Sign into your account
        </span>
        <form id="stripe-login">
          <div className={styles["padding-bottom--24"]}>
            <label htmlFor="email">Email</label>
            <input type="email" name="email"></input>
          </div>
          <div className={styles["padding-bottom--24"]}>
            <div className={styles["forgot-password"]}>
              <label htmlFor="password">Password</label>
              <div>
                <a href="#">Forgot your password?</a>
              </div>
            </div>
            <input type="password" name="password"></input>
          </div>
          <div className={styles.chechbox_styling}>
            <label htmlFor="checkbox">
              {" "}
              <input type="checkbox" name="checkbox"></input> Stay signed in for
              a week
            </label>
          </div>
          <div className={styles["padding-bottom--24"]}>
            <Button onClick={onContinueHandler}>Continue</Button>
          </div>
          <div className={styles.field}>
            <span>
              Don't have an account? <a href="" onClick={onSignUpHandler}>Sign up</a>
            </span>
          </div>
        </form>
      </div>
    </div>
  );
};
export default Login;
