  BaseComponent,
  LaunchDecorator,
  DinnerDecorator,
  FreeParkingDecorator,
  FitnessDecorator,
  ConferenceHallDecorator,
} from "./ServiceDecorator.js";
import Button from "../UI/Button/Button";
import ListService from "./ListService";

const Service = (props) => {
  const hotelServices = [
    { type: "Lunch", src: "https://img.icons8.com/fluency-systems-regular/48/lunch.png", alt:'' },
    { type: "Dinner", src: "https://img.icons8.com/ios/50/dinner.png", alt:'' },
    { type: "Parking", src: "https://img.icons8.com/ios/50/parking.png", alt:'' },
    { type: "Fitness", src: "https://img.icons8.com/ios/50/bench-press-with-dumbbells.png", alt:'bench-press-with-dumbbells' },
    { type: "Conference Room", src: "https://img.icons8.com/ios/50/meeting-room.png", alt:'meeting-room' }
  ];

  // const baseService=new BaseComponent();
  // const launch=new LaunchDecorator(baseService);
  // const dinner=new DinnerDecorator(launch);
  // const parking=new FreeParkingDecorator(dinner)
  // console.log(parking.operation());
  // console.log(parking.getAmount());

  return (
    <div className={styles.serviceContainer}>
      <h2>Service's component</h2>
      <p>
        Hotel provided a free breakfast, but it just a start service. <br></br>
        So you can choose additional service on your own)
      </p>
      <form onSubmit={onSubmitFormSeviceHandler}>
        <ListService items={hotelServices}></ListService>
        <Button>Check price</Button>
      </form>
    </div>
  );
};
export default Service;
