import React, { useState } from "react";
import "./ItemService.css";

const ItemService = (props) => {
  const [dinnerStatus, setDinnerStatus] = useState(false);
  const [lunchStatus, setLunchStatus] = useState(false);
  const [parkingStatus, setParkingStatus] = useState(false);
  const [fitnessStatus, setFitnessStatus] = useState(false);
  const [roomStatus, setRoomStatus] = useState(false);
  function onChangeHandler(event) {
    console.log(event.target.value);
    switch (event.target.value) {
      case "Dinner":
        setDinnerStatus((prev) => !prev);
        break;
      case "Lunch":
        setDinnerStatus((prev) => !prev);
        break;
      case "Parking":
        setDinnerStatus((prev) => !prev);
        break;
      case "Fitness":
        setDinnerStatus((prev) => !prev);        break;
      case "Conference Room":
        setDinnerStatus((prev) => !prev);
    }
  }

  return (
    <div className="checkbox">
      <label className="checkbox-wrapper">
        <input
          type="checkbox"
          className="checkbox-input"
          onChange={onChangeHandler}
          value={props.name}
        />
        <span className="checkbox-tile">
          <span className="checkbox-icon">
            <img width="50" height="50" src={props.src} alt={props.alt} />
          </span>
          <span className="checkbox-label">{props.name}</span>
        </span>
      </label>
    </div>
  );
};
export default ItemService;
