import React, { useState } from "react";
import styles from "./Reservation.module.css";
import { Room } from "./ReservationStrategy.js";
import Button from "../UI/Button/Button.js";

const Reservation = (props) => {
  const [priceRoom, setPriceRoom] = useState("100");
  const [nameUser, setNameUser] = useState('');
  const [surnameUser, setSurnameUser] = useState('');
  const [idUser, setIDUser] = useState('');
  //const [User, setNameUser] = useState('');
  // const adm=new HotelAdministration();
  // const res=new Residents();
  // const res1=new Residents();

  //  res.Attach(adm);
  //  res.SatisfactionLevelSet=2;

  //  res1.Attach(adm);
  //  res1.SatisfactionLevelSet=33;
  function onChangeName(event){
    setNameUser(event.target.value);
    console.log(event.target.value);
  }
  function onSubmitForm() {
    const room = new Room();

    room.Reservation();
  }
  return (
    <div className={styles.reservationContainer}>
      <h2>Reservation section</h2>
      <form onSubmit={onSubmitForm()}>
        <label htmlFor="nameUser">Your Name:</label>
        <input type="text" name="nameUser" onChange={onChangeName}></input>
        <label htmlFor="surnameUser">Your Surname:</label>
        <input type="text" name="surnameUser"></input>
        <label htmlFor="cityUser">Your city:</label>
        <input type="text" name="cityUser"></input>
        <label htmlFor="idUser">Passport ID:</label>
        <input type="number" name="idUser"></input>
        <label htmlFor="roomUser">Room's type:</label>
        <div>
          <select name="roomUser" className={styles.sectionBox}>
            <option value="100">Standart room</option>
            <option value="200">Middle room</option>
            <option value="300">Luxury room</option>
          </select>
          <p>Price: {priceRoom}$</p>
        </div>
        <Button type="submit">Reservation</Button>
      </form>
    </div>
  );
};
export default Reservation;
