import React, { useState } from "react";
import Button from "../UI/Button/Button";
import styles from "./WorkersAdd.module.css";
import {
  Manager,
  WorkersCommand as workers,
  AddWorkersCommand,
  FireWorkersCommand,
} from "./WorkersCommand.ts";
import { getAllWorker } from "../../classes/Employee.ts";

//Table
const WorkersMessage = (props) => {
  let employees = [];
  const [employeeId, setEmployeeId] = useState("");

  async function onClickHandler() {
    employees = await getAllWorker.then((employee) => {
      return employee;
    });
    function onChangeIdHandler(event) {
      setEmployeeId(event.target.value);
    }
    function onSubmitFormHandler(event) {
      event.preventDefault();
    }
  }
  return (
    <div className={styles.workersAddContainer}>
      <h2>Check employees</h2>
      <form>
        <label htmlFor="employeeId">Employee's id:</label>
        <input
          type="number"
          name="employeeId"
          value={employeeId}
          onChange={onChangeIdHandler}
        ></input>
        <Button onClick={onClickHandler}>Send</Button>
      </form>
    </div>
  );
};
export default WorkersMessage;
