import React, { useState } from "react";
import "./ItemService.css";
import {
    BaseComponent,
    LaunchDecorator,
    DinnerDecorator,
    FreeParkingDecorator,
    FitnessDecorator,
    ConferenceHallDecorator,
  } from "./ServiceDecorator.js";

const ItemService = (props) => {

  const [dinnerStatus, setDinnerStatus] = useState(false);
  const [lunchStatus, setLunchStatus] = useState(false);
  const [parkingStatus, setParkingStatus] = useState(false);
  const [fitnessStatus, setFitnessStatus] = useState(false);
  const [roomStatus, setRoomStatus] = useState(false);
  function onChangeHandler(event) {
    console.log(event.target.value);
    switch (event.target.value) {
      case "Dinner":
        setDinnerStatus((prev) => !prev);
        break;
      case "Lunch":
        setLunchStatus((prev) => !prev);
        break;
      case "Parking":
        setParkingStatus((prev) => !prev);
        break;
      case "Fitness":
        setFitnessStatus((prev) => !prev);
        break;
      case "Conference Room":
        setRoomStatus((prev) => !prev);
        break;
    }
    props.sendArrToList([dinnerStatus,lunchStatus,parkingStatus,fitnessStatus,roomStatus]);
    props.sendArrToList([{type:'Dinner',status:dinnerStatus},{type:'Lunch',status:dinnerStatus},{type:'Dinner',status:dinnerStatus},{type:'Dinner',status:dinnerStatus},{type:'Dinner',status:dinnerStatus}]);
  }

    // const baseService=new BaseComponent();
  // const launch=new LaunchDecorator(baseService);
  // const dinner=new DinnerDecorator(launch);
  // const parking=new FreeParkingDecorator(dinner)
  // console.log(parking.operation());
  // console.log(parking.getAmount());
  return (
    <div className="checkbox">
      <label className="checkbox-wrapper">
        <input
          type="checkbox"
          className="checkbox-input"
          onChange={onChangeHandler}
          value={props.name}
        />
        <span className="checkbox-tile">
          <span className="checkbox-icon">
            <img width="50" height="50" src={props.src} alt={props.alt} />
          </span>
          <span className="checkbox-label">{props.name}</span>
        </span>
      </label>
    </div>
  );
};

export default ItemService;
