import { Employee, getWorker } from "../../classes/Employee.ts";

//Управління персоналом - Pattern Команда
export interface ICommand {
  Execute(): void;
}
//Invoker
export class Manager {
  //'Invoker'
  private commands: ICommand[] = [];
  private name: string;

  constructor(name: string = "Andriy") {
    this.name = name;
  }

  public SetCommand(com: ICommand): void {
    this.commands.push(com);
    console.log("command setted");
  }

  public ExecuteCommands(): void {
    for (const command of this.commands) {
      console.log(command);
      command.Execute();
    }
    console.log(this.commands);
    this.commands = [];
  }
}

export class WorkersCommand {
  public ListWorkers: string[] = [];
